INSERT INTO ECommerceDB.dbo.Banners (Title,Subtitle,Description,[Image],ButtonText,ButtonLink,BackgroundColor,IsActive,DisplayOrder,CreatedAt,UpdatedAt) VALUES
	 (N'ƯU ĐÃI ĐẶC BIỆT',N'Giảm giá lên đến 50%',N'Khám phá bộ sưu tập đồ uống và thực phẩm cao cấp với mức giá tốt nhất. Ưu đãi có hạn, đừng bỏ lỡ!',N'https://images.unsplash.com/photo-1607082350899-7e105aa886ae?ixlib=rb-4.0.3&w=1200',N'MUA NGAY',N'/products?category=all',N'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',1,1,'2025-11-05 12:52:56.903','2025-11-05 12:52:56.903'),
	 (N'RƯỢU & BIA CAO CẤP',N'Bộ sưu tập quốc tế',N'Khám phá những loại rượu và bia đặc sắc từ khắp nơi trên thế giới. Chất lượng đảm bảo, giao hàng nhanh chóng.',N'https://images.unsplash.com/photo-1544145945-f90425340c7e?ixlib=rb-4.0.3&w=1200',N'KHÁM PHÁ',N'/products?category=alcohol',N'linear-gradient(135deg, #f093fb 0%, #f5576c 100%)',1,2,'2025-11-05 12:52:56.903','2025-11-05 12:52:56.903'),
	 (N'THỰC PHẨM TƯƠI NGON',N'Bánh kẹo & Đồ ngọt',N'Thưởng thức những món ăn vặt thơm ngon, hấp dẫn. Đảm bảo an toàn vệ sinh thực phẩm và chất lượng tốt nhất.',N'https://images.unsplash.com/photo-1555939594-58d7cb561ad1?ixlib=rb-4.0.3&w=1200',N'ĐẶT HÀNG',N'/products?category=food',N'linear-gradient(135deg, #4facfe 0%, #00f2fe 100%)',1,3,'2025-11-05 12:52:56.903','2025-11-05 12:52:56.903'),
	 (N'CÀ PHÊ ĐẶC SẢN',N'Hương vị tuyệt hảo',N'Trải nghiệm những loại cà phê rang xay nguyên chất, hương vị đậm đà khó quên. Pha chế hoàn hảo cho mọi gu thưởng thức.',N'https://images.unsplash.com/photo-1509042239860-f550ce710b93?ixlib=rb-4.0.3&w=1200',N'THỬ NGAY',N'/products?subcategory=coffee',N'linear-gradient(135deg, #43e97b 0%, #38f9d7 100%)',1,4,'2025-11-05 12:52:56.903','2025-11-05 12:52:56.903'),
	 (N'SIÊU KHUYẾN MÃI',N'Mua 2 Tặng 1',N'Chương trình khuyến mãi đặc biệt: Mua 2 sản phẩm bất kỳ được tặng 1 sản phẩm cùng loại. Áp dụng có điều kiện.',N'https://images.unsplash.com/photo-1607082350920-66b2202e03b9?ixlib=rb-4.0.3&w=1200',N'XEM CHI TIẾT',N'/promotion',N'linear-gradient(135deg, #fa709a 0%, #fee140 100%)',1,5,'2025-11-05 12:52:56.903','2025-11-05 12:52:56.903');
